(printf "Tell me how to fix every error and/or warning. The following are the diagnostics from the TypeScript compiler and ESLint. Tell me exactly what lines I need to modify in what file and what I need to change them to.\n\n";e=$(mktemp);npx eslint . --no-color --format=json|jq -r '.[] | select(any(.messages[]?;true)) | "\(.messages)",.filePath'>"$e";grep -v '^/' "$e";t=$(mktemp);npx tsc --noEmit --incremental false --pretty false 2>&1|tee "$t";f=$(mktemp);{ grep '^/' "$e";awk -F'[(]' '{print $1}' "$t";}|awk -v d="$PWD" '{p=$0;gsub(/^[[:space:]]+|[[:space:]]+$/, "", p);if (p=="") next;if (p !~ /^\//) p=d "/" p;print p;}'|while IFS= read -r p;do realpath "$p" 2>/dev/null||printf "%s\n" "$p";done|sort -u>"$f";while IFS= read -r f; do [ -f "$f" ] || continue;echo "\n## $f";cat -n "$f";done <"$f";rm -f "$e" "$t" "$f")|pbcopy
